mapper.select:
SELECT * FROM 
	(SELECT ROWNUM AS NUM,B.* FROM 
		(SELECT * FROM BOARD


					${searchSelect}  LIKE  '%' || #{searchWord} || '%'	


					AND CATEGORY  LIKE  '%' || #{cateSelect} || '%'	


			ORDER BY REGDATE DESC
		) B
	) 
WHERE NUM >= #{start} AND NUM <= #{end}

SELECT * FROM 
	(SELECT ROWNUM AS NUM, B.* FROM 
		(SELECT * FROM BOARD


					${searchSelect}  LIKE  '%' || #{searchWord} || '%'	


					AND CATEGORY  LIKE  '%' || #{cateSelect} || '%'	


			ORDER BY REGDATE DESC
		) B
	) 
WHERE NUM = #{targetNum}
