mapper.select:
SELECT * FROM 
	(SELECT ROWNUM AS NUM,B.* FROM 
		(SELECT * FROM BOARD

				WHERE ${searchSelect}  LIKE  '%' || #{searchWord} || '%'

			ORDER BY REGROUP DESC, RELEVEL ASC
		) B
	) 
WHERE NUM >= #{start} AND NUM <= #{end}

mapper.update:
UPDATE BOARD SET RELEVEL = RELEVEL + 1 
WHERE REGROUP = #{reGroup} AND 
RELEVEL   >  #{reLevel}
